import { Injectable } from '@angular/core';
import { ModalService } from './modal.service';
import { Observable } from 'rxjs';
import { IModalResult } from './model/i-modal-result';
import { DialogComponent } from './dialog/dialog.component';
import { map } from 'rxjs/operators';
import { ModalCloseReason } from './model/modal-close-reason.enum';

@Injectable({
  providedIn: 'root',
})
export class DialogService {
  constructor(private _modal: ModalService) {}

  alert(message: string): void {
    this.createDialog(message, 'common.ok', null, 'primary', false);
  }

  confirm(
    message: string,
    buttonText: string = 'common.ok',
    buttonIcon: string = null,
    buttonPalette: string = 'primary'
  ): Observable<boolean> {
    return this.createDialog(
      message,
      buttonText,
      buttonIcon,
      buttonPalette,
      true
    ).pipe(
      map((result: IModalResult) => result.reason === ModalCloseReason.resolve)
    );
  }

  private createDialog(
    message: string,
    buttonText: string = 'common.ok',
    buttonIcon: string = null,
    buttonPalette: string = 'primary',
    showCancelButton: boolean = true
  ): Observable<IModalResult> {
    const dialog = this._modal.create(
      DialogComponent,
      {
        message,
        buttonText,
        buttonIcon,
        buttonPalette,
        showCancelButton,
      },
      {
        esc: true,
        backdrop: true,
      }
    );
    return dialog.onClose;
  }
}

import {
  ApplicationRef,
  ComponentFactoryResolver,
  ComponentRef,
  Injectable,
  Injector,
  Renderer2,
  RendererFactory2,
  TemplateRef,
  Type,
} from '@angular/core';
import { TetaContentRef } from '../contract/teta-content-ref';
import { DynamicData } from '../contract/dynamic-data';

@Injectable({
  providedIn: 'root',
})
export class DynamicComponentService {
  private _renderer: Renderer2;

  constructor(
    private _componentFactoryResolver: ComponentFactoryResolver,
    private _rendererFactory: RendererFactory2,
    private _appRef: ApplicationRef
  ) {
    this._renderer = this._rendererFactory.createRenderer(null, null);
  }

  createComponent<T>(
    component: Type<T>,
    contentRef: TetaContentRef,
    injector: Injector,
    container: any
  ): ComponentRef<T> {
    const componentRef = this._componentFactoryResolver
      .resolveComponentFactory(component)
      .create(injector, contentRef.nodes);
    this._appRef.attachView(componentRef.hostView);
    container.appendChild(componentRef.location.nativeElement);
    return componentRef;
  }

  createContent(
    content: string | TemplateRef<any> | Type<any>,
    injector: Injector,
    context?: any
  ): TetaContentRef {
    if (content === null || content === undefined) {
      throw new Error('Content is undefined');
    }
    if (typeof content === 'string') {
      return this.fromString(content);
    } else if (content instanceof TemplateRef) {
      return this.fromTemplate(content, context);
    } else {
      return this.fromComponent(content, injector, context);
    }
  }

  destroy<T>(
    component: ComponentRef<T>,
    content: TetaContentRef,
    container: HTMLElement
  ): void {
    if (component) {
      this._appRef.detachView(component.hostView);
      component.destroy();
    }
    if (content && content.viewRef) {
      content.viewRef.destroy();
    }
    content = null;
  }

  getContext(
    content: string | TemplateRef<any> | Type<any>,
    context: any
  ): any {
    if (content instanceof TemplateRef) {
      return {
        $implicit: context,
        data: context,
      };
    }
    return context;
  }

  getInjector(data: DynamicData, parent: Injector): Injector {
    return Injector.create({
      providers: [
        {
          provide: DynamicData,
          useValue: data,
        },
      ],
      parent,
    });
  }

  private fromString(content: string): TetaContentRef {
    return new TetaContentRef([[this._renderer.createText(`${content}`)]]);
  }

  private fromTemplate(
    content: TemplateRef<any>,
    context: any
  ): TetaContentRef {
    const viewRef = content.createEmbeddedView(context);
    this._appRef.attachView(viewRef);
    return new TetaContentRef([viewRef.rootNodes], viewRef);
  }

  private fromComponent(
    content: any,
    injector: Injector,
    context: any
  ): TetaContentRef {
    const componentFactory =
      this._componentFactoryResolver.resolveComponentFactory<any>(content);
    const componentRef = componentFactory.create(injector);
    for (const key in context) {
      if (context.hasOwnProperty(key)) {
        componentRef.instance[key] = context[key];
      }
    }
    const componentNativeEl = componentRef.location.nativeElement;
    this._appRef.attachView(componentRef.hostView);
    return new TetaContentRef(
      [[componentNativeEl]],
      componentRef.hostView,
      componentRef
    );
  }
}

import {Inject, Injectable, Renderer2, RendererFactory2} from '@angular/core';
import {DOCUMENT} from '@angular/common';
import {HttpBackend, HttpClient} from '@angular/common/http';
import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class IconService {
  private static _loaded: string[] = [];
  private static _pending: string[] = [];
  private _renderer: Renderer2;

  constructor(@Inject(DOCUMENT) protected _document: any,
              private _rendererFactory: RendererFactory2,
              private _httpBackend: HttpBackend,
              private _http: HttpClient) {
    this._renderer = this._rendererFactory.createRenderer(null, null);
  }

  addSprite(url: string, bypassInterceptors: boolean = true) {
    if (IconService._loaded.indexOf(url) < 0 && IconService._pending.indexOf(url) < 0) {
      IconService._pending.push(url);
      this.getSVG(url, bypassInterceptors).subscribe(svg => {
        IconService._pending = IconService._pending.filter(_ => _ !== url);
        IconService._loaded.push(url);
        this._renderer.insertBefore(this._document.body, svg, this._document.body.firstChild);
      });
    }
  }

  private getSVG(url: string, bypassInterceptors: boolean = true): Observable<SVGElement> {
    const http = bypassInterceptors ? new HttpClient(this._httpBackend) : this._http;
    return http.get(url, {responseType: 'text'})
      .pipe(
        map((svgText: string) => {
          const svgEl = this.svgElementFromString(svgText);
          return this.cloneSVG(svgEl);
        })
      );
  }

  private svgElementFromString(str: string): SVGElement | never {
    const div = this._renderer.createElement('DIV');
    div.innerHTML = str;
    const svg = div.querySelector('svg') as SVGElement;
    if (!svg) {
      throw new Error('No SVG found in loaded contents');
    }
    return svg;
  }

  private cloneSVG(svg: SVGElement): SVGElement {
    return svg.cloneNode(true) as SVGElement;
  }
}

import {Injectable} from '@angular/core';
