import {
  annotationBadge,
  annotationCallout,
  annotationCalloutCircle,
  annotationCalloutCurve,
  annotationCalloutElbow,
  annotationCalloutRect,
  annotationCustomType,
  annotationLabel,
  annotationXYThreshold,
} from 'd3-svg-annotation';
import { BasePoint } from './point/base-point';

const annotationMap = [
  annotationBadge,
  annotationLabel,
  annotationCallout,
  annotationCalloutCircle,
  annotationCalloutCurve,
  annotationCalloutElbow,
  annotationCalloutRect,
  annotationCustomType,
  annotationXYThreshold,
] as const;

type AnnotationTypes = typeof annotationMap[number];

export class Annotation {
  point: BasePoint;
  yAxisIndex: number;
  xAxisIndex: number;
  type?: AnnotationTypes;
  enabled?: boolean;
  fillColor?: string;
  borderWidth?: number;
  borderColor?: string;
  dx?: number;
  dy?: number;
  className?: string;
  connector?: {
    end: string;
  };
  note?: {
    label?: string;
    title?: string;
    bgPadding?: {
      top?: number;
      right?: number;
      bottom?: number;
      left?: number;
    };
    bgRadius?: boolean;
  };

  constructor(options?: {
    point: BasePoint;
    type?: AnnotationTypes;
    yAxisIndex: number;
    xAxisIndex: number;
    enabled?: boolean;
    fillColor?: string;
    borderWidth?: number;
    borderColor?: string;
    dx?: number;
    dy?: number;
    className?: string;
    connector?: {
      end: string;
    };
    note?: {
      label?: string;
      title?: string;
      bgPadding?: {
        top?: number;
        right?: number;
        bottom?: number;
        left?: number;
      };
      bgRadius?: boolean;
    };
  }) {
    this.point = options?.point;
    this.yAxisIndex = options?.yAxisIndex;
    this.xAxisIndex = options?.xAxisIndex;
    this.type = options?.type || annotationLabel;
    this.enabled = options?.enabled;
    this.className = options?.className;
    this.fillColor = options?.fillColor;
    this.borderColor = options?.borderColor;
    this.borderWidth = options?.borderWidth;
    this.note = options?.note;
    this.connector = options?.connector;
    this.dx = options?.dx;
    this.dy = options?.dy;
  }
}

import { IDrawer } from '../model/i-drawer';
import { BasePoint } from '../model/point/base-point';
import { Series } from '../model/series';
import * as d3 from 'd3';

export class AreaDrawer implements IDrawer<BasePoint> {
  draw(
    series: Series<BasePoint>,
    context: d3.Selection<SVGElement, unknown, null, undefined>,
    scaleX: any,
    scaleY: any
  ): void {
    const points = series.data;

    const path = d3
      .area()
      .x1((d: any) => (d.x1 != null ? scaleX(d.x1) : scaleX(0)))
      .x0((d: any) => scaleX(d.x))
      .y((d: any) => scaleY(d.y));

    context
      .append('path')
      .attr('transform', `translate(${0}, 0)`)
      .attr('fill', series.color)
      .attr('stroke', series.color)
      .attr('stroke-width', series.strokeWidth ? series.strokeWidth : 1)
      .datum(points)
      .attr('d', path as any);
  }
}

export class ArrayUtil {
  public static asArray<T>(value: T | T[]): T[] {
    if (value === null || value === undefined) {
      return [];
    }
    if (value instanceof Array) {
      return value;
    }
    return [value];
  }

  public static flatten(
    data: any[],
    children?: string | ((item: any) => any[]),
    onlyLeafs?: boolean
  ): any[] {
    const result: any[] = [];
    data.forEach((child) => {
      let childItems: any[];
      if (children) {
        if (typeof children === 'string') {
          childItems = child[children];
        }
        if (typeof children === 'function') {
          childItems = children(child);
        }
      } else {
        childItems = child.children;
      }
      if (
        childItems !== null &&
        childItems !== undefined &&
        childItems.length > 0
      ) {
        if (!onlyLeafs) {
          result.push(child);
        }
        result.push(...ArrayUtil.flatten(childItems, children, onlyLeafs));
      } else {
        result.push(child);
      }
    });
    return result;
  }

  static findRecursive(
    tree: any[],
    comparer: (iterableNode: any) => boolean,
    children: string = 'children'
  ): any {
    const found = tree.find((x) => comparer(x));
    if (found !== null && found !== undefined) {
      return found;
    }
    for (let i = 0, l = tree.length; i < l; i++) {
      const item = tree[i];
      if (item[children] && item[children].length) {
        const res = ArrayUtil.findRecursive(item[children], comparer);
        if (res !== null && res !== undefined) {
          return res;
        }
      }
    }
    return null;
  }

  static filterRecursive(
    array: any[],
    filter: (item: any) => boolean,
    children: string = 'children',
    keepChildren: boolean = true
  ): any {
    const result = [];
    if (array && array.length) {
      for (const item of array) {
        const resultItem = Object.assign({}, item);
        if (filter(resultItem)) {
          result.push(resultItem);
          if (!keepChildren) {
            resultItem[children] = [];
            break;
          }
        } else if (item[children] && item[children].length > 0) {
          resultItem[children] = [];
          const found = ArrayUtil.filterRecursive(
            item[children],
            filter,
            children,
            keepChildren
          );
          if (found?.length > 0) {
            resultItem[children] = found;
            result.push(resultItem);
          }
        }
      }
    }
    return result;
  }

  static distinct<T>(array: T[], comparator: (item: T) => any): T[] {
    const result: T[] = [];
    if (array && array.length) {
      for (const item of array) {
        const found = result.find((_) => comparator(_) === comparator(item));
        if (!found) {
          result.push(item);
        }
      }
    }
    return result;
  }

  static findParents(tree: any[], item: any) {
    if (tree.indexOf(item) >= 0) {
      return tree;
    }
    if (Array.isArray(tree)) {
      for (const treeNode of tree) {
        const childResult = this.findParents(treeNode.children, item);
        if (Array.isArray(childResult)) {
          return [treeNode].concat(childResult);
        }
      }
    }
  }

  static moveItem(list: any[], sourceIndex, targetIndex) {
    const res = [...list];
    const item = list[sourceIndex];
    if (targetIndex > sourceIndex) {
      res.splice(targetIndex, 0, item);
      res.splice(sourceIndex, 1);
    } else {
      res.splice(sourceIndex, 1);
      res.splice(targetIndex, 0, item);
    }
    return res;
  }
}

import { IChartConfig } from '../../model/i-chart-config';
import { AxisOrientation } from '../../model/enum/axis-orientation';
import { Series } from '../../model/series';
import { BasePoint } from '../../model/base-point';
import * as d3 from 'd3';
import { AxisOptions } from '../../model/axis-options';
import { AxisSizeBuilder, ExtremesBuilder } from './builders/public-api';
import { generateTicks } from '../utils/public-api';
import { ScaleType } from '../../model/enum/scale-type';

export class Axis {
  private chartConfig: IChartConfig;
  private _orientation: AxisOrientation;
  private _index: number;
  private _extremes: [number, number] = [0, 0];
  private _selfSize: number;
  private _ticksValues: number[];
  private _options: AxisOptions;
  private _isFake: boolean;

  private defaultFormatters = new Map<ScaleType, any>()
    .set(ScaleType.linear, d3.format(',.2f'))
    .set(ScaleType.time, d3.timeFormat('%B %d, %Y'))
    .set(ScaleType.log, d3.format(',.2f'))
    .set(ScaleType.pow, d3.format(',.2f'))
    .set(ScaleType.sqrt, d3.format(',.2f'));

  constructor(config: IChartConfig) {
    this.chartConfig = config;
  }

  /**
   * Factory for creating x,y axes
   * @param {AxisOrientation} orientation
   * Axis type
   * @param {IChartConfig} config
   * Chart config
   * @param {number} index
   * Index axis
   * @param {boolean} isFake
   * @return {Axis}
   * New generated axis
   */
  public static createAxis(
    orientation: AxisOrientation,
    config: IChartConfig,
    index: number,
    isFake = false
  ): Axis {
    const axis = new Axis(config);
    axis.setLocate(orientation);
    axis.setIndex(index);

    axis.setOptions();
    axis.setExtremes();
    axis.setTicksValues();
    axis.setSelfSize();

    axis._isFake = isFake;

    return axis;
  }

  /**
   *
   * @param {orientation} orientation
   * Set locate axis x or y
   */
  private setLocate(orientation: AxisOrientation): void {
    this._orientation = orientation;
  }

  /**
   *
   * @param {number} index
   * Index axis
   */
  private setIndex(index: number): void {
    this._index = index;
  }

  /**
   * @return {Array<Series<BasePoint>>}
   * Linked series
   */
  public linkedSeries(): Array<Series<BasePoint>> {
    const linkedFilter = (serie: Series<BasePoint>) =>
      serie[
        this._orientation === AxisOrientation.y ? 'yAxisIndex' : 'xAxisIndex'
      ] === this._index;

    return this.chartConfig?.series.filter(linkedFilter);
  }

  private setExtremes(): void {
    const builder = new ExtremesBuilder();
    this._extremes = builder.build(this);
  }

  private setSelfSize(): void {
    this._selfSize = new AxisSizeBuilder().build(this);
  }

  private setTicksValues(): void {
    this._ticksValues = generateTicks(this._extremes);
  }

  private setOptions(): void {
    const options =
      this.orientation === AxisOrientation.x
        ? this.chartConfig.xAxis[this.index]
        : this.chartConfig.yAxis[this.index];

    this._options = options;
  }

  get extremes(): Array<number> {
    return this._extremes;
  }

  get orientation(): AxisOrientation {
    return this._orientation;
  }

  get selfSize(): number {
    return this._selfSize;
  }

  get tickValues(): number[] {
    return this._ticksValues;
  }

  get index(): number {
    return this._index;
  }

  get options(): AxisOptions {
    return this._options;
  }

  get isFake(): boolean {
    return this._isFake;
  }

  public defaultFormatter() {
    return this.defaultFormatters.get(this.options.scaleType.type);
  }
}

import * as d3 from 'd3';
import {AxisType} from '../model/enum/axis-type';
import {Series} from '../model/series';
import {BasePoint} from '../model/point/base-point';
import {ChartOptions} from '../model/chart-options';
import {AxisOptions} from '../model/axis-options';

export class Axis {
  public static offsetFactor = 22;
  public readonly width: number;

  private _type: AxisType;
  private _chartOptions: ChartOptions;
  private _extents: [number, number] = [0, 0];
  private _index: number;
  private _height: number;
  private _offset: number;
  private _ticks: any;

  constructor(options: {
    type: AxisType;
    index: number;
    chartOptions: ChartOptions;
    height?: number;
  }) {
    this._type = options?.type;
    this._index = options?.index || 0;
    this._chartOptions = options?.chartOptions;
    this._height = options?.height;

    if (this._chartOptions == null) {
      throw new Error(`No chartOptions for axis ${AxisType[this._type]}`);
    }

    this.createExtents();
    this.width = this.calculateAxisWidth();
  }

  get index() {
    return this._index;
  }

  get options(): AxisOptions {
    return this._type === AxisType.abscissa
      ? this._chartOptions.xAxis[this.index]
      : this._chartOptions.yAxis[this.index];
  }

  get extent(): [number, number] {
    return this._extents;
  }

  get type(): AxisType {
    return this._type;
  }

  get offset(): number {
    return this._offset;
  }

  get ticks() {
    return this._ticks;
  }

  setOffset(offset: number) {
    this._offset = offset;
  }

  private calculateAxisWidth(): number {
    if (this.type === AxisType.abscissa) {
      return 0;
    }

    const y = d3
      .scaleLinear()
      .domain([this.extent[0], this.extent[1]])
      .range([0, this._height])
      .nice();

    const body = d3
      .select('body')
      .append('svg')
      .attr('class', 'calculate-offset')
      .style('position', 'absolute')
      .style('left', '-9999px');
    const axis = d3.axisRight(y).tickValues(this.ticks);
    const element = body.append('g').attr('class', 'font-caption').call(axis);

    const bBox = element.node().getBBox();

    d3.selectAll('.calculate-offset').remove();

    return bBox.width;
  }

  private createExtents() {
    const hasMin = this.options?.min != null;
    const hasMax = this.options?.max != null;

    if (!hasMin || !hasMax) {
      const filtered = this._chartOptions?.series?.filter(
        (serie: Series<BasePoint>) =>
          this._index ===
          serie[this._type === AxisType.abscissa ? 'xAxisIndex' : 'yAxisIndex']
      );

      const raw = filtered.map((series: Series<BasePoint>) =>
        d3.extent(series?.data, (point: BasePoint) =>
          this._type === AxisType.abscissa ? point.x : point.y
        )
      );

      const merged = [].concat(...(raw ?? []));

      const abs = (num) => Math.abs(num);

      this._extents = this.options.negative
        ? [-Math.abs(d3.max(merged.map(abs))), d3.max(merged.map(abs))]
        : [d3.min(merged), d3.max(merged)];
    }

    if (hasMin) {
      this._extents[0] = this.options?.min;
    }

    if (hasMax) {
      this._extents[1] = this.options?.max;
    }
  }
}

