import { StringFilter } from './string-filter';
import { NumericFilter } from './numeric-filter';
import { DateFilter } from './date-filter';
import { ListFilter } from './list-filter';
import { SortParam } from './sort-param';
import { DateFilterValue } from './date-filter-value';
import { NumericFilterValue } from './numeric-filter-value';
import { ListFilterType } from '../enum/list-filter-type.enum';
import { BooleanFilter } from './boolean-filter';

export class FilterState {
  stringFilters: StringFilter[] = [];
  numericFilters: NumericFilter[] = [];
  dateFilters: DateFilter[] = [];
  listFilters: ListFilter[] = [];
  booleanFilters: BooleanFilter[] = [];
  sortParams: SortParam[] = [];

  constructor(options?: {
    stringFilters?: any[];
    booleanFilters?: any[];
    numericFilters?: any[];
    dateFilters?: any[];
    listFilters?: any[];
    sortParams?: any[];
  }) {
    if (options) {
      if (options.stringFilters && options.stringFilters.length) {
        this.stringFilters = options.stringFilters.map(
          (x: any) => new StringFilter(x)
        );
      }

      if (options.booleanFilters && options.booleanFilters.length) {
        this.booleanFilters = options.booleanFilters.map(
          (x: any) => new BooleanFilter(x)
        );
      }

      if (options.numericFilters && options.numericFilters.length) {
        this.numericFilters = options.numericFilters.map(
          (x: any) => new NumericFilter(x)
        );
      }

      if (options.dateFilters && options.dateFilters.length) {
        this.dateFilters = options.dateFilters.map(
          (x: any) => new DateFilter(x)
        );
      }

      if (options.listFilters && options.listFilters.length) {
        this.listFilters = options.listFilters.map(
          (x: any) => new ListFilter(x)
        );
      }

      if (options.sortParams && options.sortParams.length) {
        this.sortParams = options.sortParams.map((x: any) => new SortParam(x));
      }
    }
  }

  static restore(cookie: string): any {
    const state = localStorage.getItem(cookie) || 'null';
    return JSON.parse(state);
  }

  save(cookieName: string): void {
    if (cookieName) {
      localStorage.setItem(cookieName, JSON.stringify(this));
    }
  }

  addListFilter(value: ListFilter): ListFilter {
    if (this.listFilters === null || this.listFilters === undefined) {
      this.listFilters = [];
    }
    const filter = this.listFilters.find((f) => f.field === value.field);
    if (filter === null || filter === undefined) {
      this.listFilters.push(value);
      return value;
    } else {
      filter.value = value.value;
      return filter;
    }
  }

  addDateFilter(value: DateFilter): DateFilter {
    if (this.dateFilters === null || this.dateFilters === undefined) {
      this.dateFilters = [];
    }
    const filter = this.dateFilters.find((f) => f.field === value.field);
    if (filter === null || filter === undefined) {
      this.dateFilters.push(value);
      return value;
    } else {
      filter.value = value.value;
      return filter;
    }
  }

  addStringFilter(value: StringFilter): StringFilter {
    if (this.stringFilters === null || this.stringFilters === undefined) {
      this.stringFilters = [];
    }
    const filter = this.stringFilters.find((f) => f.field === value.field);
    if (filter === null || filter === undefined) {
      this.stringFilters.push(value);
      return value;
    } else {
      filter.value = value.value;
      return filter;
    }
  }

  addBooleanFilter(value: BooleanFilter): BooleanFilter {
    if (this.booleanFilters === null || this.booleanFilters === undefined) {
      this.booleanFilters = [];
    }
    const filter = this.booleanFilters.find((f) => f.field === value.field);
    if (filter === null || filter === undefined) {
      this.booleanFilters.push(value);
      return value;
    } else {
      filter.value = value.value;
      return filter;
    }
  }

  addNumericFilter(value: NumericFilter): NumericFilter {
    if (this.numericFilters === null || this.numericFilters === undefined) {
      this.numericFilters = [];
    }
    const filter = this.numericFilters.find((f) => f.field === value.field);
    if (filter === null || filter === undefined) {
      this.numericFilters.push(value);
      return value;
    } else {
      filter.value = value.value;
      return filter;
    }
  }

  clear(): void {
    this.sortParams.length = 0;

    this.stringFilters.map((f: StringFilter) => {
      f.value = '';
    });
    this.booleanFilters.map((f: BooleanFilter) => {
      f.value = null;
    });
    this.dateFilters.map((f: DateFilter) => {
      f.value = new DateFilterValue({
        lessThan: null,
        greaterThan: null,
      });
    });
    this.numericFilters.map((f: NumericFilter) => {
      f.value = new NumericFilterValue({
        lessThan: null,
        greaterThan: null,
        equalsTo: null,
      });
    });
    this.listFilters.map((f: ListFilter) => {
      f.value = [];
      f.type = ListFilterType.None;
    });
  }
}

import {
  AbstractControl,
  FormControl,
  FormGroup,
  ValidationErrors,
  Validators,
} from '@angular/forms';
import { TableColumn } from '../component/table/contract/table-column';
import { ArrayUtil } from '../common/util/array-util';

export class FormsUtil {
  static validateAllFormFields(formGroup: FormGroup) {
    Object.keys(formGroup.controls).forEach((field) => {
      const control = formGroup.get(field);
      if (control instanceof FormControl) {
        control.markAsTouched({ onlySelf: true });
        control.markAsDirty({ onlySelf: true });
      } else if (control instanceof FormGroup) {
        this.validateAllFormFields(control);
      }
    });
  }

  static controlIsInvalid(formGroup: FormGroup, controlName: string) {
    return (
      formGroup.controls[controlName]?.invalid &&
      formGroup.controls[controlName]?.dirty
    );
  }

  static matchValuesValidator(
    matchTo: string
  ): (control: AbstractControl) => ValidationErrors | null {
    return (control: AbstractControl): ValidationErrors | null =>
      !!control.parent &&
      !!control.parent.value &&
      control.value === control.parent.controls[matchTo].value
        ? null
        : { isMatching: false };
  }

  static requiredIf(
    value: boolean
  ): (control: AbstractControl) => ValidationErrors | null {
    return (control: AbstractControl): ValidationErrors | null =>
      !!control.parent &&
      !!control.parent.value &&
      control.value != null &&
      value
        ? null
        : { isMatching: false };
  }

  static initFormFromColumns(columns: TableColumn[], dataItem: any) {
    const form = new FormGroup({});
    const flat = ArrayUtil.flatten(columns, 'columns');
    flat.forEach((column: TableColumn) => {
      const control = new FormControl(
        {
          value: dataItem ? dataItem[column.name] : undefined,
          disabled: !column.editable,
        },
        FormsUtil.getValidators(column)
      );
      form.registerControl(column.name, control);
    });
    return form;
  }

  static getValidators(column: TableColumn) {
    const validators = [];
    if (column.required) {
      validators.push(Validators.required);
    }
    if (column.minValue != null) {
      validators.push(Validators.min(column.minValue));
    }
    if (column.maxValue != null) {
      validators.push(Validators.max(column.maxValue));
    }
    return validators;
  }
}

import * as d3 from 'd3';
import { ILegendDrawer, LegendDrawOptions } from '../model/i-drawer-legend';
import { BasePoint } from '../model/point/base-point';

export class GradientDrawer implements ILegendDrawer {
  draw(options: LegendDrawOptions<BasePoint>): void {
    const container = d3
      .select(options.context.parentElement)
      .select('.legend-container');

    container.selectAll('.gradient-legend').remove();

    const svg = container
      .append('svg')
      .attr('class', 'gradient-legend')
      .attr('width', options.width)
      .attr('height', 32);

    svg.selectAll('.legend-defs').remove();

    const extent = d3.extent(
      options.series[0]?.data as any,
      (d: any) => d.value
    ) as any;

    const defs = svg.append('defs').attr('class', 'legend-defs') as any;

    const linearGradient = defs
      .append('linearGradient')
      .attr('id', 'legend-gradient');

    linearGradient
      .selectAll('stop')
      .data([...options.series[0]?.data].reverse())
      .enter()
      .append('stop')
      .attr(
        'offset',
        (d) => ((d.value - extent[0]) / (extent[1] - extent[0])) * 100 + '%'
      )
      .attr('stop-color', (d) => d.color);

    const g = svg.append('g').attr('class', 'gradient-legend');

    g.append('rect')
      .attr('width', options.width - 80)
      .attr('transform', 'translate(40, 0)')
      .attr('height', 4)
      .style('fill', 'url(#legend-gradient)');

    const xScale = d3
      .scaleLinear()
      .range([80, options.width - 80])
      .domain(extent)
      .nice();
    const tickCount = options.width / 80;

    const xAxis = d3.axisBottom(xScale).tickSize(8).ticks(tickCount);

    g.call(xAxis).call((node) => {
      node.select('.domain').remove();
      node.selectAll('line').remove();
    });
  }
}

