import {IRect} from '../contract/i-rect';
import {Align} from '../enum/align.enum';
import {VerticalAlign} from '../enum/vertical-align.enum';

export class PositionUtil {
  public static getPosition(
    containerPosition: IRect,
    elementPosition: IRect,
    align: Align,
    verticalAlign: VerticalAlign,
    margin: number = 0,
    verticalMargin: number = 0,
    transformedParentRect: IRect = {
      left: 0,
      right: 0,
      top: 0,
      bottom: 0
    }
  ): IRect {
    const rect: IRect = {};
    const elementWidth = elementPosition.right - elementPosition.left;
    const elementHeight = elementPosition.bottom - elementPosition.top;
    const containerWidth = containerPosition.right - containerPosition.left;
    const containerHeight = containerPosition.bottom - containerPosition.top;
    if (align === Align.left) {
      rect.left = containerPosition.left + margin;
    }
    if (align === Align.right) {
      rect.left = containerPosition.right - elementWidth - margin;
    }
    if (align === Align.center) {
      rect.left =
        (containerPosition.left + containerPosition.right) / 2 -
        elementWidth / 2;
    }
    if (align === Align.fitWidth) {
      rect.left = containerPosition.left + margin;
      rect.right = window.innerWidth - containerPosition.right + margin;
    }

    if (align === Align.minWidth) {
      rect.left = containerPosition.left + margin;
      rect.minWidth = containerWidth;
    }

    if (verticalAlign === VerticalAlign.auto) {
      if (
        containerPosition.bottom +
        (elementPosition.bottom - elementPosition.top) <=
        window.innerHeight
      ) {
        verticalAlign = VerticalAlign.bottom;
      } else {
        verticalAlign = VerticalAlign.top;
      }
    }
    if (verticalAlign === VerticalAlign.top) {
      rect.top = containerPosition.top - elementHeight - verticalMargin;
    }
    if (verticalAlign === VerticalAlign.bottom) {
      rect.top = containerPosition.bottom + verticalMargin;
    }
    if (verticalAlign === VerticalAlign.center) {
      rect.top =
        (containerPosition.top + containerPosition.bottom) / 2 -
        elementHeight / 2;
    }

    if (rect.left < 0) {
      rect.left = 0;
    }
    if (rect.top + elementHeight > window.innerHeight || rect.bottom < 0) {
      rect.bottom = 0;
    }
    if (rect.top < 0) {
      rect.top = 0;
    }
    if (verticalAlign === VerticalAlign.bottom || verticalAlign === VerticalAlign.center) {
      rect.maxHeight = window.innerHeight - rect.top;
    }
    if (verticalAlign === VerticalAlign.top) {
      rect.maxHeight = containerPosition.top;
    }
    rect.left = rect.left - transformedParentRect.left;
    rect.right = rect.right - transformedParentRect.left;
    rect.top = rect.top - transformedParentRect.top;
    rect.bottom = rect.bottom ? rect.bottom - transformedParentRect.bottom : rect.bottom;
    return rect;
  }

  public static setElementPosition(element: HTMLElement, rect: IRect): void {
    element.style.left = rect.left != null ? `${rect.left}px` : '';
    element.style.right = rect.right != null ? `${rect.right}px` : '';
    element.style.top = rect.top != null ? `${rect.top}px` : '';
    element.style.bottom = rect.bottom != null ? `${rect.bottom}px` : '';
    element.style.maxHeight =
      rect.maxHeight != null ? `${rect.maxHeight}px` : '';
    element.style.minWidth = rect.minWidth != null ? `${rect.minWidth}px` : '';
  }

  public static getMaxHeight(position: IRect): number | null {
    let maxHeight = null;
    maxHeight = 1;
    return maxHeight;
  }

  public static getMaxWidth(position: IRect): number | null {
    let maxWidth = null;
    maxWidth = 1;
    return maxWidth;
  }
}

import { IDrawer } from '../model/i-drawer';
import { Series } from '../model/series';
import * as d3 from 'd3';
import { ScatterPoint } from '../model/point/scatter-point';
import { ChartOptions } from '../model/chart-options';

export class ScatterDrawer implements IDrawer<ScatterPoint> {
  private _virtualCanvas: any;
  private _colorToData = {};
  private _options: ChartOptions;
  private _context:
    | d3.Selection<SVGElement, unknown, null, undefined>
    | d3.Selection<HTMLCanvasElement, unknown, null, undefined>;

  draw(
    series: Series<ScatterPoint>,
    context:
      | d3.Selection<SVGElement, unknown, null, undefined>
      | d3.Selection<HTMLCanvasElement, unknown, null, undefined>,
    scaleX: any,
    scaleY: any,
    options: ChartOptions
  ): void {
    this._options = options;

    d3.select(context.node().parentNode as any).select('.virtual-canvas').remove();

    if (series.renderTo === 'canvas') {
      this._context = context;

      const height = context.node().clientHeight;
      const width = context.node().clientWidth;

      const points = series.data;

      const context2D = (context.node() as HTMLCanvasElement).getContext('2d');
      this._virtualCanvas = d3
        .select(context.node().parentNode as any)
        .append('canvas')
        .attr('class', 'virtual-canvas')
        .style('display', 'none');

      this._virtualCanvas.attr('width', width).attr('height', height);
      const virtualContext = this._virtualCanvas.node().getContext('2d');

      context.on('mouseout', () => {
        d3.select(this._context.node().parentNode as any)
          .select('.tooltip-chart')
          .style('display', 'none');

        context.on('mousemove', null);
      });

      context.on('mouseenter', () => {
        d3.select(this._context.node().parentNode as any)
          .select('.tooltip-chart')
          .style('display', null);
        context.on('mousemove', (e) => this.handleMouseMove(e));
      });

      points.forEach((d: any, idx) => {
        const color = this.getColor(idx);
        this._colorToData[color] = d;
        virtualContext.fillStyle = color;
        context2D.fillStyle = d.color;

        const cx = scaleX(d.x);
        const cy = scaleY(d.y);

        context2D.beginPath();
        context2D.arc(cx, cy, d.radius, 0, 2 * Math.PI);
        context2D.closePath();
        context2D.fill();

        virtualContext.beginPath();
        virtualContext.arc(cx, cy, d.radius, 0, 2 * Math.PI);
        virtualContext.closePath();
        virtualContext.fill();
      });
    }
  }

  private getColor(index) {
    return d3
      .rgb(
        Math.floor(index / 256 / 256) % 256,
        Math.floor(index / 256) % 256,
        index % 256
      )
      .toString();
  }

  private tooltipPosition(event) {
    const centerX = this._context.node().clientWidth / 2;
    const centerY = this._context.node().clientHeight / 2;

    const padding = { x: 10, y: 10 };

    const scene = {
      left: event.pageX > centerX ? 'initial' : `${event.pageX + padding.x}px`,
      top: event.pageY > centerY ? 'initial' : `${event.pageY + padding.y}px`,
      bottom:
        event.pageY > centerY
          ? `${window.innerHeight - event.pageY}px`
          : 'initial',
      right:
        event.pageX > centerX
          ? `${window.innerWidth - event.pageX + padding.x}px`
          : 'initial',
    };

    return scene;
  }

  private handleMouseMove(event: MouseEvent) {
    const mouse = d3.pointer(event);

    const virtualContext = this._virtualCanvas.node().getContext('2d');

    const image = virtualContext.getImageData(mouse[0], mouse[1], 1, 1);
    const color = d3.rgb.apply(null, image.data).toString();
    const possibleDatum = this._colorToData[color];

    const { top, right, bottom, left } = this.tooltipPosition(event);

    if (possibleDatum) {
      const formatted = this._options.tooltip?.format([possibleDatum]);
      d3.select(this._context.node().parentNode as any)
        .select('.tooltip-chart')
        .style('z-index', 3)
        .style('top', top)
        .style('right', right)
        .style('bottom', bottom)
        .style('left', left)
        .html(formatted);
    }
  }
}

import {BasePoint} from './point/base-point';
import {SeriesType} from './enum/series-type';
import {IDrawer} from './i-drawer';
import * as d3 from 'd3';

export class Series<T extends BasePoint> {
  id?: number;
  renderTo?: 'canvas' | 'svg';
  type: SeriesType;
  curveType?: d3.CurveFactoryLineOnly | d3.CurveFactory;
  data: T[];
  axisFormat?: () => void;
  name?: string;
  xAxisIndex?: number;
  yAxisIndex?: number;
  drawer?: IDrawer<T>;
  strokeWidth?: number;
  strokeDasharray?: string;
  visible?: boolean;
  color?: string;
  colorScale?: d3.ScaleSequential<any, any>;
  drag?: {
    enable?: boolean;
    extendLine?: boolean;
    grabbers?: {
      radius?: number;
      stroke?: string;
      fill?: string;
      strokeWidth?: string;
    };
  };
  extend?: boolean;
  showInLegend?: boolean;

  constructor(options?: {
    id?: number;
    renderTo?: 'canvas' | 'svg';
    type?: SeriesType;
    curveType?: d3.CurveFactoryLineOnly | d3.CurveFactory;
    data?: T[];
    axisFormat?: () => void;
    name?: string;
    xAxisIndex?: number;
    yAxisIndex?: number;
    drawer?: IDrawer<T>;
    strokeWidth?: number;
    strokeDasharray?: string;
    visible?: boolean;
    color?: string;
    colorScale?: d3.ScaleSequential<any, any>;
    drag?: {
      enable?: boolean;
      extendLine?: boolean;
      grabbers?: {
        radius?: number;
        stroke?: string;
        fill?: string;
        strokeWidth?: string;
      };
    };
    extend?: boolean;
    showInLegend?: boolean;
  }) {
    this.id = options?.id;
    this.renderTo = options?.renderTo ?? 'svg';
    this.type = options?.type != null ? options?.type : SeriesType.line;
    this.curveType =
      options?.curveType != null ? options?.curveType : d3.curveLinear;
    this.data = options?.data?.filter((_) => !isNaN(_.x) && !isNaN(_.y));
    this.axisFormat = options?.axisFormat;
    this.name = options?.name;
    this.xAxisIndex = options?.xAxisIndex != null ? options?.xAxisIndex : 0;
    this.yAxisIndex = options?.yAxisIndex != null ? options?.yAxisIndex : 0;
    this.drawer = options?.drawer;
    this.strokeWidth = options?.strokeWidth;
    this.strokeDasharray = options?.strokeDasharray ?? null;
    this.visible = options?.visible || true;
    this.color = options?.color ?? 'cyan';
    this.colorScale = options?.colorScale;
    this.drag = {enable: false, extendLine: false, ...options?.drag};
    this.showInLegend =
      options?.showInLegend != null ? options?.showInLegend : true;
    this.extend = options?.extend != null ? options?.extend : false;
  }
}

