export class SortParam {
  /**
   * Поле сортировки
   */
  field: string;
  /**
   * Направление (true: Asc, false: Desc)
   */
  asc: boolean;
  /**
   * Порядок сортировки
   */
  order: number;

  /**
   * Инициализация из анонимного объекта
   */
  constructor(options?: {
    field?: string;
    asc?: boolean;
    order?: number;
  }) {
