import {
  // ContentChildren,
  Directive,
  EventEmitter,
  HostListener,
  Input,
  Output,
  // QueryList,
} from '@angular/core';
import {DragSortEvent} from './drag-sort-event';
// import {DragSortItemDirective} from './drag-sort-item.directive';

@Directive({
  selector: '[tetaDragSortContainer]',
})
export class DragSortContainerDirective<T> {
  @Input() dragSortList: T[];
  @Output() dragSorted: EventEmitter<DragSortEvent<T>> = new EventEmitter<DragSortEvent<T>>();

  // @ContentChildren(DragSortItemDirective, {descendants: true})
  // private items: QueryList<DragSortItemDirective<T>>;
  private _dragItem: T;

  constructor() {
  }

  @HostListener('drop', ['$event']) drop(event: DragEvent): void {
    event.stopPropagation();
    event.preventDefault();
  }

  setDragItem(item: T) {
    this._dragItem = item;
  }

  getDragItem(): T {
    return this._dragItem;
  }

  getList(): T[] {
    return this.dragSortList;
  }

  setList(list: T[]) {
    this.dragSortList = list;
  }

  updateSortOrder(source: T, target: T, insertBefore: boolean) {
    if (source === target) {
      return;
    }
    const list = this.getList();
    const sourceIndex = list.indexOf(source);
    const targetIndex = list.indexOf(target);
    if (
      (insertBefore && sourceIndex + 1 === targetIndex) ||
      (!insertBefore && sourceIndex === targetIndex + 1)
    ) {
      return;
    }
    const newIndex = list.indexOf(target) + (insertBefore ? 0 : 1);

    this.dragSorted.emit({
      list,
      source,
      target,
      newIndex,
      previousIndex: sourceIndex,
    });
  }
}

import {
  Directive,
  ElementRef,
  Host,
  HostBinding,
  HostListener,
  Input,
  OnDestroy,
  OnInit,
  Renderer2,
} from '@angular/core';
import {DragSortContainerDirective} from './drag-sort-container.directive';

@Directive({
  selector: '[tetaDragSortItem]',
})
export class DragSortItemDirective<T> implements OnInit, OnDestroy {
  @Input() tetaDragSortItem: T;
  @Input() dragSortDirection: 'horizontal' | 'vertical' = 'horizontal';

  @HostBinding('attr.draggable') private readonly draggable = true;
  @HostBinding('class.position-relative') private readonly relative = true;

  private _dragElement: HTMLElement;

  private rect: any;

  constructor(
    @Host() private _container: DragSortContainerDirective<T>,
    private _elementRef: ElementRef,
    private _renderer: Renderer2
  ) {
  }

  @HostListener('dragstart', ['$event']) dragstart(event: DragEvent): void {
    this._container.setDragItem(this.tetaDragSortItem);
  }

  @HostListener('dragenter', ['$event']) dragenter(event: DragEvent): void {
    this.rect = this._elementRef.nativeElement.getBoundingClientRect();
  }

  @HostListener('dragover', ['$event']) dragover(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();
    if (this.rect && this._container.getDragItem()) {
      this.showDrag(this.getDropPosition(event));
    }
  }

  @HostListener('dragleave', ['$event']) dragleave(event: DragEvent): void {
    event.preventDefault();
    this.hideDrag();
  }

  @HostListener('dragend', ['$event']) dragend(event: DragEvent): void {
    this._container.setDragItem(null);
    this.hideDrag();
  }

  @HostListener('drop', ['$event']) drop(event: DragEvent): void {
    event.stopPropagation();
    this._container.updateSortOrder(
      this._container.getDragItem(),
      this.tetaDragSortItem,
      this.getDropPosition(event) === 'start'
    );
    this.hideDrag();
  }

  ngOnInit() {
    this.createDrag();
  }

  ngOnDestroy() {
    this.destroyDrag();
  }

  private showDrag(position: 'start' | 'end') {
    this.hideDrag();
    this._renderer.addClass(this._dragElement, `drop-direction-${position}`);
  }

  private hideDrag() {
    this._renderer.removeClass(this._dragElement, 'drop-direction-start');
    this._renderer.removeClass(this._dragElement, 'drop-direction-end');
  }

  private createDrag() {
    this._dragElement = this._renderer.createElement('div');
    this._renderer.appendChild(
      this._elementRef.nativeElement,
      this._dragElement
    );
    this._renderer.addClass(this._dragElement, 'drop-direction');
    this._renderer.addClass(
      this._dragElement,
      this.dragSortDirection === 'horizontal'
        ? 'drop-direction-horizontal'
        : 'drop-direction-vertical'
    );
  }

  private destroyDrag() {
    this._renderer.removeChild(this._renderer, this._dragElement);
  }

  private getDropPosition(event: DragEvent) {
    if (this.dragSortDirection === 'horizontal') {
      return event.clientX >= this.rect.x + this.rect.width / 2
        ? 'end'
        : 'start';
    } else {
      return event.clientY >= this.rect.y + this.rect.height / 2
        ? 'end'
        : 'start';
    }
  }
}

import {
  ChangeDetectorRef,
  ContentChild,
  Directive,
  ElementRef,
  EventEmitter,
  HostListener,
  Input,
  NgZone,
  Output,
  Renderer2,
} from '@angular/core';
import {DomUtil} from '../../common/util/dom-util';
import {PositionUtil} from '../../common/util/position-util';
import {Align} from '../../common/enum/align.enum';
import {VerticalAlign} from '../../common/enum/vertical-align.enum';
import {DropdownHeadDirective} from './dropdown-head.directive';
import {DropdownContentDirective} from './dropdown-content.directive';
import {AutoCloseIgnoreCase} from '../../common/contract/auto-close-ignore-case';
import {IRect} from '../../common/contract/i-rect';
import {takeWhile, throttleTime} from 'rxjs/operators';

@Directive()
// eslint-disable-next-line @angular-eslint/directive-class-suffix
export class DropdownBase {
  @Input() align: Align = Align.left;
  @Input() verticalAlign: VerticalAlign = VerticalAlign.bottom;
  @Input() appendToBody: boolean;
  @Input() disabled: boolean;
  @Input() shadow = true;
  @Input() className: string | string[];

  @Input()
  set open(val: boolean) {
    if (val != null && val !== this._open && !this.disabled) {
      if (val) {
        this.openDropdown();
      } else {
        this.closeDropdown();
      }
    }
  }

  get open(): boolean {
    return this._open;
  }

  get container() {
    return this.appendToBody
      ? this._document.body
      : this._elementRef.nativeElement;
  }

  @Output() openChange: EventEmitter<boolean> = new EventEmitter<boolean>();
  @Input() autoClose = true;
  @Input() autoCloseIgnore: Array<AutoCloseIgnoreCase> = ['inside'];

  @ContentChild(DropdownHeadDirective, {
    static: false,
    read: ElementRef,
  })
  protected _head: ElementRef;

  @ContentChild(DropdownContentDirective, {static: false})
  protected _content: DropdownContentDirective;

  protected _body: HTMLElement | null = null;
  protected _open = false;
  protected _alive = true;

  constructor(
    protected _cdr: ChangeDetectorRef,
    protected _document: any,
    protected _elementRef: ElementRef<HTMLElement>,
    protected _zone: NgZone,
    protected _renderer: Renderer2
  ) {
    this._zone.onStable
      .pipe(
        takeWhile((_) => this._alive),
        throttleTime(10, undefined, {trailing: true})
      )
      .subscribe((_) => {
        if (this._head?.nativeElement && this._body) {
          setTimeout(() => {
            if (this._head?.nativeElement && this._body) {
              this.setPosition(this._head.nativeElement, this._body);
            }
          });
        }
      });
  }

  @HostListener('click', ['$event']) click(event: MouseEvent): void {
    if (this.open) {
      if (!DomUtil.clickedInside(this._body, event)) {
        this.closeDropdown();
      }
    } else {
      if (DomUtil.clickedInside(this._head.nativeElement, event)) {
        this.openDropdown();
      }
    }
  }

  @HostListener('document:click', ['$event']) documentClick(
    event: MouseEvent
  ): void {
    if (
      !this.open ||
      !this.autoClose ||
      event.detail === 0 ||
      DomUtil.clickedInside(this._head.nativeElement, event) ||
      DomUtil.clickedInside(this._body, event)
    ) {
      return;
    }
    if (DomUtil.clickedInside(this._body, event)) {
      if (this.autoCloseIgnore.indexOf('inside') < 0) {
        this.closeDropdown();
      }
    } else {
      if (this.autoCloseIgnore.indexOf('outside') < 0) {
        this.closeDropdown();
      }
    }
  }

  @HostListener('window:keyup', ['$event']) keyUp(event: KeyboardEvent): void {
    if (
      !this.open ||
      !this.autoClose ||
      ['esc', 'enter'].every(
        (_: 'esc' | 'enter') => this.autoCloseIgnore.indexOf(_) >= 0
      )
    ) {
      return;
    }
    if (
      (event.code === 'Escape' && this.autoCloseIgnore.indexOf('esc') < 0) ||
      (event.code === 'Enter' && this.autoCloseIgnore.indexOf('enter') < 0)
    ) {
      this.closeDropdown();
    }
  }

  protected closeDropdown(): void {
    if (this.open && this._body && this.container.contains(this._body)) {
      this._renderer.removeChild(this.container, this._body);
      this._open = false;
      this._body = null;
      this.openChange.emit(this.open);
    }
  }

  private openDropdown(): void {
    if (this._open || !this._content || this.disabled) {
      return;
    }
    this._open = true;
    this.openChange.emit(this.open);
    const renderer = this._renderer;
    const content = this._content.nativeElement;
    const container = (this._body =
      this._body || renderer.createElement('div'));
    renderer.addClass(container, 'dropdown');
    if (this.className != null) {
      if (this.className instanceof Array && this.className.length > 0) {
        this.className.forEach((_) => {
          renderer.addClass(container, _);
        });
      }
      if (typeof this.className === 'string') {
        renderer.addClass(container, this.className);
      }
    }

    renderer.appendChild(container, content);
    renderer.appendChild(this.container, container);

    this.setPosition(this._head.nativeElement, this._body);
  }

  private setPosition(container: HTMLElement, target: HTMLElement): void {
    const containerPosition = container.getBoundingClientRect();
    const targetPosition = target.getBoundingClientRect();

    const rect: IRect = {
      bottom: containerPosition.bottom,
      top: containerPosition.top,
      left: containerPosition.left,
      right: containerPosition.right,
    };

    const targetRect: IRect = {
      bottom: targetPosition.bottom,
      top: targetPosition.top,
      left: targetPosition.left,
      right: targetPosition.right,
    };

    const targetTransformedParent = DomUtil.findTransformedParent(target);
    let parentPosition = {
      left: 0,
      right: 0,
      top: 0,
      bottom: 0
    };
    if (targetTransformedParent) {
      parentPosition = targetTransformedParent.getBoundingClientRect();
    }

    const position = PositionUtil.getPosition(
      rect,
      targetRect,
      this.align,
      this.verticalAlign,
      0,
      0,
      parentPosition
    );

    PositionUtil.setElementPosition(target, position);
  }
}

