import {
  ChangeDetectorRef,
  ComponentRef,
  Directive,
  ElementRef,
  EventEmitter,
  Injector,
  Input,
  NgZone,
  OnDestroy,
  OnInit,
  Output,
  TemplateRef,
  Type,
} from '@angular/core';
import { DynamicComponentService } from '../common/service/dynamic-component.service';
import { Align } from '../common/enum/align.enum';
import { VerticalAlign } from '../common/enum/vertical-align.enum';
import { filter, takeWhile } from 'rxjs/operators';
import { TetaContentRef } from '../common/contract/teta-content-ref';
import { PopupContentComponent } from '../component/dynamic-component/popup-content/popup-content.component';

@Directive()
export abstract class DynamicContentBaseDirective implements OnInit, OnDestroy {
  @Input() data: any;
  @Input() className: string | string[];
  @Input() align: Align = Align.left;
  @Input() verticalAlign: VerticalAlign = VerticalAlign.bottom;
  @Input() appendToBody: boolean;
  @Input() set open(open: boolean) {
    this._open = open;
    if (this._open) {
      this.createContentRef();
    } else {
      this.destroyContentRef();
    }
  }
  @Output() openChange: EventEmitter<boolean> = new EventEmitter<boolean>();

  protected _alive = true;
  protected _componentRef: ComponentRef<any>;
  protected _content: TetaContentRef;
  protected _open = false;

  protected abstract get _dynamicContent():
    | string
    | TemplateRef<any>
    | Type<any>;

  protected constructor(
    protected _document: any,
    protected _elementRef: ElementRef,
    protected _service: DynamicComponentService,
    protected _injector: Injector,
    protected _zone: NgZone,
    protected _cdr: ChangeDetectorRef
  ) {
    this._zone.onStable
      .pipe(
        takeWhile((_) => this._alive),
        filter((_) => this._open)
      )
      .subscribe((_) => {
        this.setPosition();
      });
  }

  ngOnDestroy(): void {
    this._alive = false;
    this.destroyContentRef();
  }

  ngOnInit(): void {}

  protected createContentRef<T>(
    className?: string | string[]
  ): ComponentRef<T> {
    if (!this._componentRef) {
      this._open = true;
      const injector = this._service.getInjector(this.data, this._injector);
      const context = this._service.getContext(this._dynamicContent, this.data);
      this._content = this._service.createContent(
        this._dynamicContent,
        this._injector,
        context
      );
      this._componentRef = this._service.createComponent(
        PopupContentComponent,
        this._content,
        injector,
        this.appendToBody ? this._document.body : this._elementRef.nativeElement
      );
      if (className) {
        this._componentRef.instance.addClass(className);
      }
    }
    return this._componentRef;
  }

  protected destroyContentRef(): void {
    if (this._open) {
      this._open = false;
      this._service.destroy(
        this._componentRef,
        this._content,
        this.appendToBody ? this._document.body : this._elementRef.nativeElement
      );
      this._componentRef = null;
    }
  }

  protected abstract setPosition(): void;
}

