@Directive({
  selector: '[tetaNoAutofill]',
})

/**
 * Disable autofill inputs in Chrome. Append hidden inputs with text/password type, to element.
 */
export class NoAutofillDirective implements OnInit {
  constructor(private _renderer: Renderer2, private _elementRef: ElementRef) {}

  ngOnInit() {
    const inputsTypes = ['text', 'password'];

    inputsTypes.forEach((_) => {
      const inputElement = this._renderer.createElement('input');
      this._renderer.setAttribute(inputElement, 'type', _);
      this._renderer.setAttribute(inputElement, 'class', 'position-absolute');
      this._renderer.setStyle(inputElement, 'left', '-9999px');
      this._renderer.insertBefore(
        this._elementRef.nativeElement,
        inputElement,
        this._elementRef.nativeElement.firstChild
      );
    });
  }
}

import {Directive, ElementRef, HostListener, Input} from '@angular/core';
import {NgControl} from '@angular/forms';

@Directive({
  selector: '[tetaOnlyNumber]',
})
export class OnlyNumberDirective {
  @Input() tetaOnlyNumber = true;
  @Input() allowDecimals = true;
  @Input() allowSign = true;
  @Input() decimalSeparator = '.';
  @Input() commaSeparator = ',';

  private _previousValue = '';

  private _integerUnsigned = '^[0-9]*$';
  private _integerSigned = '^-?[0-9]+$';
  private _decimalUnsigned = '^[0-9]+(.[0-9]+)?$';
  private _decimalSigned = '^-?[0-9]+(.[0-9]+)?$';

  private readonly _minusSign: string = '-';

  constructor(private _elementRef: ElementRef, private _control: NgControl) {
  }

  @HostListener('change', ['$event']) onChange(e: any) {
    if (this.tetaOnlyNumber === false) {
      return;
    }
    this.validateValue(this._elementRef.nativeElement.value);
  }

  @HostListener('paste', ['$event']) onPaste(e: any) {
    if (this.tetaOnlyNumber === false) {
      return;
    }
    this.validateValue(e.clipboardData.getData('text/plain'));
    e.preventDefault();
  }

  @HostListener('keydown', ['$event']) onKeyDown(e: KeyboardEvent) {
    if (this.tetaOnlyNumber === false) {
      return;
    }
    const cursorPosition: number =
      (e.target as HTMLInputElement).selectionStart ?? 0;
    const originalValue: string = (e.target as HTMLInputElement).value;
    const controlOrCommand = e.ctrlKey === true || e.metaKey === true;
    const signExists = originalValue.includes('-');
    const separatorExists =
      originalValue.includes(this.decimalSeparator) ||
      originalValue.includes(this.commaSeparator);

    const allowedKeys = [
      'Backspace',
      'ArrowLeft',
      'ArrowRight',
      'Delete',
      'Escape',
      'Tab',
      'Home',
      'End',
    ];

    const separatorIsCloseToSign = signExists && cursorPosition <= 1;
    if (this.allowDecimals && !separatorIsCloseToSign && !separatorExists) {
      // if (this.decimalSeparator === '.') {
      allowedKeys.push('.');
      // } else {
      // allowedKeys.push(',');
      // }
    }

    const firstCharacterIsSeparator =
      originalValue.charAt(0) !== this.decimalSeparator;
    if (
      this.allowSign &&
      !signExists &&
      firstCharacterIsSeparator &&
      cursorPosition === 0
    ) {
      allowedKeys.push('-');
    }

    if (
      allowedKeys.indexOf(e.key) !== -1 ||
      (e.code === 'KeyA' && controlOrCommand) ||
      (e.code === 'KeyC' && controlOrCommand) ||
      (e.code === 'KeyV' && controlOrCommand) ||
      (e.code === 'KeyZ' && controlOrCommand) ||
      (e.code === 'KeyX' && controlOrCommand)
    ) {
      return;
    }
    this._previousValue = originalValue;

    const isNumber = new RegExp(this._integerUnsigned).test(e.key);
    if (isNumber) {
      return;
    } else {
      if (e.key === ',' && originalValue.indexOf('.') < 0) {
        this._elementRef.nativeElement.value = originalValue + '.';
      }
      e.preventDefault();
    }
  }

  validateValue(value: string): void {
    if (this.tetaOnlyNumber === false) {
      return;
    }
    value = value.replace(',', '.').trim();
    let regex: string = this._integerUnsigned;
    if (!this.allowDecimals && !this.allowSign) {
      regex = this._integerUnsigned;
    }
    if (!this.allowDecimals && this.allowSign) {
      regex = this._integerSigned;
    }
    if (this.allowDecimals && !this.allowSign) {
      regex = this._decimalUnsigned;
    }
    if (this.allowDecimals && this.allowSign) {
      regex = this._decimalSigned;
    }

    let firstCharacter = value.charAt(0);
    if (firstCharacter === this.decimalSeparator) {
      value = 0 + value;
    }

    const lastCharacter = value.charAt(value.length - 1);
    if (lastCharacter === this.decimalSeparator) {
      value = value + 0;
    }

    let signedValue = false;
    if (firstCharacter === this._minusSign) {
      signedValue = true;
      value = value.substring(1);
      firstCharacter = value.charAt(0);
    }
    let secondChar = value.charAt(1);
    while (
      firstCharacter === '0' &&
      secondChar !== '' &&
      secondChar !== this.decimalSeparator
      ) {
      value = value.substring(1);
      firstCharacter = value.charAt(0);
      secondChar = value.charAt(1);
    }
    if (signedValue === true) {
      value = this._minusSign + value;
    }

    const valueParts = value.split(this.decimalSeparator);
    const naturalPart = valueParts?.[0];
    let decimalPart = valueParts?.[1];

    if (decimalPart != null && /^0+$/.test(decimalPart)) {
      decimalPart = '0';
      value = naturalPart + '.' + decimalPart;
    }
    if (value === '-0') {
      value = '0';
    }
    if (value === '-0.0') {
      value = '0.0';
    }
    const valid: boolean = new RegExp(regex).test(value.toString());
    this._control.control.setValue(valid ? value : this._previousValue ?? 0);
  }
}

import {
  Directive,
  EventEmitter,
  HostBinding,
  HostListener,
  Inject,
  Input,
  Output,
} from '@angular/core';
import { DOCUMENT } from '@angular/common';

@Directive({
  selector: '[tetaResizeDrag]',
})
export class ResizeDragDirective {
  @Input() tetaResizeDrag: 'horizontal' | 'vertical' = 'vertical';

  @Output() resizeStart = new EventEmitter<MouseEvent>();
  @Output() resizeProcess = new EventEmitter<MouseEvent>();
  @Output() resizeEnd = new EventEmitter<MouseEvent>();

  @HostBinding('class.resize-drag_active')
  private _active: boolean;

  @HostBinding('class.resize-drag_horizontal')
  private get horizontal() {
    return this.tetaResizeDrag === 'horizontal';
  }

  @HostBinding('class.resize-drag_vertical')
  private get vertical() {
    return this.tetaResizeDrag === 'vertical';
  }

  constructor(@Inject(DOCUMENT) private _document: any) {}

  @HostListener('mousedown', ['$event']) mouseDown(event: MouseEvent) {
    this.resizeStart.emit(event);
    this.addListeners();
  }

  private addListeners() {
    this._active = true;
    this._document.addEventListener('mouseup', this.handleMouseUp);
    this._document.addEventListener('mousemove', this.handleMouseMove);
  }

  private removeListeners() {
    this._active = false;
    this._document.removeEventListener('mouseup', this.handleMouseUp);
    this._document.removeEventListener('mousemove', this.handleMouseMove);
  }

  private handleMouseUp = (event: MouseEvent) => {
    this.resizeEnd.emit(event);
    this.removeListeners();
  };

  private handleMouseMove = (event: MouseEvent) => {
    this.resizeProcess.emit(event);
  };
}

