export class ZoomableDirective {
  @Input() config?: IChartConfig;
  @Input() axis?: Axis;
  @Input() size?: DOMRect;

  @HostBinding('class.zoomable') private zoomable = false;

  constructor(private element: ElementRef, private svc: ZoomService) {}

