(a: number) => {
  if (!isFinite(a)) {
    return 0;
  }
  let e = 1;
  let p = 0;
  while (Math.round(a * e) / e !== a) {
    e *= 10;
    p++;
  }
  return p;
}

(
  inputText?: string | number | null,
  backupRatio = 0.5,
  fontSize = 11
): number => {
  let text = inputText ?? '';
  text = text.toString();

  return fontSize * backupRatio * text.length;
